import { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import SidebarLeft from './Components/SidebarLeft';
import Navbar from './Components/Navbar';
import Objcard from './Components/Objcard';
import './Pkdx.css';

function Objetos() {

    const [items, setItems] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 8;
    const totalItems = 1005;

    useEffect(() => {
        fetchItems();
    }, [currentPage]);

    const fetchItems = async () => {
        try {

            const promises = [];
            const start = (currentPage - 1) * itemsPerPage + 1;
            const end = Math.min(start + itemsPerPage - 1, totalItems);
            for (let i = start; i <= end; i++) {
                promises.push(
                    await axios.get(`https://pokeapi.co/api/v2/item/${i}`));
                }

            const itemDetails = await Promise.all(
                response.data.results.map(async (item) => {
                    const itemResponse = await axios.get(item.url);

                    const nameEs = itemResponse.data.names.find(n => n.language.name === 'es')?.name || "Desconocido";

                    return {
                        name: nameEs,
                        id: itemResponse.data.id,
                        image: itemResponse.data.sprites.default,
                    };
                })
            );

            setItems(itemDetails);
        } catch (error) {
            console.error('Error al obtener los objetos:', error);
        }
    };

    const handlePrevPage = () => {
        if (currentPage > 1) setCurrentPage(currentPage - 1);
    };

    const handleNextPage = () => {
        if (currentPage * itemsPerPage < totalItems) setCurrentPage(currentPage + 1);
    };


    return (
        <>
            <div className="home-container">
                <SidebarLeft />

                <main className="main-content">
                    <Navbar />

                    <div className='Cards'>
                        <h1 className='Pkdxtitle'>Objetos</h1>

                        <div className='cards-wrapper'>

                            <button className='pagback' onClick={handlePrevPage} disabled={currentPage === 1}><img className='leftarrow' src='arrow.png'></img></button>

                            <div className='cards-container'>

                                {items.map((item) => (
                                    <Objcard
                                        key={item.id}
                                        title={item.name.charAt(0).toUpperCase() + item.name.slice(1)}
                                        text={`#${item.id}`}
                                        image={item.image}
                                        link={`${item.id}`}
                                    />
                                ))}

                            </div>

                            <button className='pagadv' onClick={handleNextPage} disabled={currentPage * itemsPerPage >= totalItems}><img className='rightarrow' src='arrow.png'></img></button>

                        </div>

                    </div>

                </main>


            </div>
        </>
    );
}

export default Objetos;
