import { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Registro.css';


function Register2() {

    
    const [nombre, setNombre] = useState('');
    const [correo, setCorreo] = useState('');
    const [pssw, setPssw] = useState('');
    const [user, setUser] = useState('');
    const [gen, setGen] = useState('');
    const [nac, setNac] = useState('');

    const [errors, setErrors] = useState({});

    const validarFormulario = () => {
        const newErrors = {};
        const regexPassword = /^(?=.*[A-Z])(?=.*\d)[A-Za-z\d]{8,}$/;
        const today = new Date().toISOString().split("T")[0];

        if (!nombre.trim()) newErrors.nombre = "El nombre es obligatorio";
        if (!correo.trim()) newErrors.correo = "El correo es obligatorio";
        if (!user.trim()) newErrors.user = "El usuario es obligatorio";
        if (!gen) newErrors.gen = "Selecciona un género";
        if (!nac) newErrors.nac = "Selecciona una fecha de nacimiento";
        else if (nac > today) newErrors.nac = "La fecha no puede ser futura";
        if (!pssw.trim()) newErrors.pssw = "La contraseña es obligatoria";
        else if (!regexPassword.test(pssw)) newErrors.pssw = "Debe tener 8 caracteres, una mayúscula y un número";

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };
    //const [pic, setPic] = useState(null);


    /*const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                const output = document.getElementById('output');
                output.src = reader.result
                setPic(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };*/

    const enviarDatos = (event) => {
        event.preventDefault();
        if (!validarFormulario()) return;
        
        axios.post("http://localhost:3001/register", {
            name: nombre,
            email: correo,
            pssw: pssw,
            user: user,
            gen: gen,
            nac: nac,
        }).then(resp => {
            alert(resp.data.message === "Registrado" ? "Se ha registrado correctamente" : "Error al registrarse");
        }).catch(() => {
            alert("Error en el servidor");
        });
    };

    return (
        <div className='regisbody'>
            <section className='regissec1'>
                <form className='regisform' onSubmit={enviarDatos}>
                    <h2>Regístrate en Pokémon Training Zone</h2>
                    <label>Nombre:</label>
                    <input onChange={(e) => setNombre(e.target.value)} type="text" />
                    {errors.nombre && <p className="error">{errors.nombre}</p>}
                    
                    <label>Correo Electrónico:</label>
                    <input onChange={(e) => setCorreo(e.target.value)} type="email" />
                    {errors.correo && <p className="error">{errors.correo}</p>}
                    
                    <label>Usuario:</label>
                    <input onChange={(e) => setUser(e.target.value)} type="text" />
                    {errors.user && <p className="error">{errors.user}</p>}
                    
                    <label>Género:</label>
                    <select onChange={(e) => setGen(e.target.value)}>
                        <option value="">Selecciona una opción</option>
                        <option value="1">Niño</option>
                        <option value="2">Niña</option>
                    </select>
                    {errors.gen && <p className="error">{errors.gen}</p>}
                    
                    <label>Fecha de Nacimiento:</label>
                    <input onChange={(e) => setNac(e.target.value)} type="date" />
                    {errors.nac && <p className="error">{errors.nac}</p>}
                    
                    <label>Contraseña:</label>
                    <input onChange={(e) => setPssw(e.target.value)} type="password" />
                    {errors.pssw && <p className="error">{errors.pssw}</p>}
                    
                    <button type="submit">Crear Cuenta</button>
                </form>
            </section>
            <section className="footer">
                <Link to="/Login" className='regislink'>¿Tienes cuenta? Inicia sesión</Link>
            </section>
        </div>
    );
}

export default Register2;
