import { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import SidebarLeft from './Components/SidebarLeft';
import SidebarRight from './Components/SidebarRight';
import Navbar from './Components/Navbar';
import './Editperfil.css';

function Edit() {

    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [user, setUser] = useState(null);
    const [dataU, setDataU] = useState([]);

    const AlertDef = (text) => {
            Swal.fire({
                title: '¡Aviso de la Pokédex!',
                text: text,
                imageUrl: 'ditto.gif',
                imageWidth: 150,
                imageHeight: 150,
                imageAlt: 'Imagen de ditto',
                timer: 2500,
                showConfirmButton: false,
                timerProgressBar: true,
                background: '#90c4d0',
                color: '#000000',
            });
        };
    
        const AlertErr = (text) => {
            Swal.fire({
                title: '¡Se ha escapado!',
                text: text,
                imageUrl: 'ditto.gif',
                imageWidth: 150,
                imageHeight: 150,
                imageAlt: 'Imagen de ditto',
                timer: 2500,
                showConfirmButton: false,
                timerProgressBar: true,
                background: '#ab3232',
                color: '#000000',
            });
        };
    
        const AlertTrue = (text) => {
            Swal.fire({
                title: '¡Capturado!',
                text: text,
                imageUrl: 'ditto.gif',
                imageWidth: 150,
                imageHeight: 150,
                imageAlt: 'Imagen de ditto',
                timer: 2000,
                showConfirmButton: false,
                timerProgressBar: true,
                background: '#3ea83d',
                color: '#000000',
            });
        };
    

    useEffect(() => {
        const token = localStorage.getItem("token");
        const userData = JSON.parse(localStorage.getItem("user"));

        if (token && userData) {
            setIsLoggedIn(true);
            setUser(userData);

            axios.get("http://localhost:3001/geteditprofile", {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
                .then((resp) => {
                    if (resp.data.msg === "Error") {
                        alert("Error al obtener la info");
                    } else if (resp.data.msg === "No info") {
                        setDataU(null);
                    } else {
                        setDataU(resp.data[0]);
                    }
                });
        } else {
            setIsLoggedIn(false);
        }
    }, []);

    const [fileImg, setFileImg] = useState(null);
    const [nombre, setNombre] = useState('');
    const [nac, setNac] = useState('');

    const validarFormulario = () => {
        if (!nombre || !user || !nac || !fileImg) {
            AlertDef("Todos los campos son obligatorios");
            return false;
        }
        if (new Date(nac) > new Date()) {
            AlertDef("La fecha de nacimiento no puede ser posterior al día de hoy");
            return false;
        }
        return true;
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                const output = document.getElementById('output');
                output.src = reader.result

            };
            reader.readAsDataURL(file);
        } else {
            console.log('No se seleccionó ninguna imagen');
        }
    };


    return (
        <>
            <div className="home-container-perfil">

                <main className="main-content-perfil">
                    <div className="container-perfil">
                        <div className="profile-image-perfil">
                            <div className="profile-icon-perfil">
                                <img className="profile-icon-perfil" src={'data:image/png;base64,' + dataU.PFP} alt='PFP'></img>
                            </div>
                            <div className="edit-icon-perfil">
                                <img src="edit.png" />
                            </div>
                        </div>

                        <div className="form-group-perfil">
                            <label className='label-perfil'>NOMBRE</label>
                            <input className='input-perfil' type="text" id="nombre" name="nombre" value={dataU.NOMBRE}/>
                        </div>

                        <div className="form-group-perfil">
                            <label className='label-perfil'>USUARIO</label>
                            <input className='input-perfil' type="text" id="usuario" name="usuario" value={dataU.USERNAME}/>
                        </div>

                        <div className="form-group-perfil">
                            <label className='label-perfil'>CUMPLEAÑOS DEL USUARIO</label>
                            <input className='input-perfil' type="date" id="cumpleanos" name="cumpleanos" value={dataU.FECNAC ? dataU.FECNAC.slice(0,10) : ''}/>
                        </div>

                        <div className="icons-perfil">
                            <div className="back-icon-perfil">
                                <Link to="/Perfil"><img src="back.png" alt="Volver" /></Link>
                            </div>
                            <div className="folder-icon-perfil">
                                <img src="save.png" alt="Guardar" />
                            </div>
                        </div>
                    </div>
                </main>

            </div>
        </>
    );
}

export default Edit;
