import React from 'react';
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import './Styles/SidebarRight.css';
import axios from 'axios';

const SidebarRight = () => {

    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("token");
        const userData = JSON.parse(localStorage.getItem("user"));

        if (token && userData) {
            setIsLoggedIn(true);
            setUser(userData);
        } else {
            setIsLoggedIn(false);
        }
    }, []);

    const [dataU, setDataU] = useState([]);

    useEffect(
        () => {
            axios.get("http://localhost:3001/gethomeuser", {
            }).then(
                (resp) => {
                    if (resp.data.msg === "Error") {
                        alert("Error al obtener la info");
                    } else if (resp.data.msg === "No info") {
                        setDataU([]);
                        alert("No hay registros");
                    } else {
                        setDataU(resp.data);
                    }
                }
            );
    },);

    return (
        <aside className="sidebar-right">
            {isLoggedIn ? (
                <>
                    <img className="profile-avatar" src="/pkball.png"></img>
                    <div className="mini-circles-list">
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                    </div>
                </>
            ) : (
                <>
                    <img className="profile-avatar" src="/pkball.png"></img>
                    <div className="mini-circles-list">
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                    </div>
                </>
            )}

        </aside>
    );
};

export default SidebarRight;
