import { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import SidebarLeft from './Components/SidebarLeft';
import SidebarRight from './Components/SidebarRight';
import Navbar from './Components/Navbar';
import './Home.css';

function Home() {

    const [randomPokemons, setRandomPokemons] = useState([6]);
    const nav = useNavigate();

    const fetchRandomPokemons = async () => {
        const total = 1025;
        const ids = new Set();

        while (ids.size < 6) {
            const randId = Math.floor(Math.random() * total) + 1;
            ids.add(randId);
        }

        const pokemonPromises = [...ids].map(id =>
            axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`)
        );

        try {
            const responses = await Promise.all(pokemonPromises);
            const data = responses.map(res => ({
                id: res.data.id,
                name: res.data.name,
                image: res.data.sprites.other['official-artwork'].front_default
            }));
            setRandomPokemons(data);
        } catch (error) {
            console.error("Error fetching random PokÃ©mon:", error);
        }
    };

    const handleClickPokemon = (id) => {
        nav(`/Pkdx?search=${id}`);
    };

    return (
        <>
            <div className="home-container">
                <SidebarLeft />

                <main className="main-content">
                    <Navbar />

                    <section className="poke-rank-section">
                        <h2 className="section-title">PokÃ© Rank</h2>
                        <div className="pokeball-list">
                            <img className="pokeball-icon" src="/mew.jpg" />
                            <img className="pokeball-icon" src="/pkball.png" />
                            <img className="pokeball-icon" src="/pkball.png" />
                            <img className="pokeball-icon" src="/pkball.png" />
                        </div>
                    </section>

                    <section className="poke-random-section">
                        <h2 className="section-title">
                            PokÃ© Random <span className="question-mark" onClick={fetchRandomPokemons}>ðŸ’­</span>
                        </h2>
                        <div className="random-pokemon-grid">
                            {randomPokemons.slice(0, 3).map(pokemon => (
                                <img
                                    key={pokemon.id}
                                    className="pokemon-bubble"
                                    src={pokemon.image}
                                    alt={pokemon.name}
                                    onClick={() => handleClickPokemon(pokemon.id)}
                                />
                            ))}
                        </div>
                        <div className="random-pokemon-grid2">
                            {randomPokemons.slice(3).map(pokemon => (
                                <img
                                    key={pokemon.id}
                                    className="pokemon-bubble2"
                                    src={pokemon.image}
                                    alt={pokemon.name}
                                    onClick={() => handleClickPokemon(pokemon.id)}
                                />
                            ))}
                        </div>
                    </section>
                </main>

                <SidebarRight />
            </div>
        </>
    );
}

export default Home;
