import { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './Login.css';
import Swal from 'sweetalert2';


function Login() {


    const [user, setUser] = useState('');
    const [pssw, setPssw] = useState('');
    const nav = useNavigate();

    const showDataRequiredWithMew = () => {
        Swal.fire({
            title: '¡Atención!',
            text: 'Por favor llena todos los datos',
            imageUrl: 'http://googleusercontent.com/image_generation_content/0',
            imageWidth: 150,
            imageHeight: 150,
            imageAlt: 'Imagen de Mew',
            confirmButtonText: 'Aceptar',
            background: '#90c4d0',
            color: '#000000',
            imageWidth: 200,
            imageHeight: 200,
            customClass: {
                image: 'mew-image',
            }
        });
    };

    const showDataRequiredAlert = (message = 'Por favor llena todos los datos', title = 'Atención') => {
        Swal.fire({
            title: title,
            text: message,
            icon: 'info', // 'info' o 'warning' podrían ser más adecuados que 'error'
            confirmButtonText: 'Aceptar',
            width: '700px',
            background: '#90c4d0', // Establece el color de fondo de la alerta
            // Opcional: Si necesitas que el texto tenga un color específico para contrastar
            // color: '#FFFFFF', // Por ejemplo, texto blanco
            // Opcional: Si quieres personalizar el color del botón de confirmación
            confirmButtonColor: '#3085d6', // El color por defecto de SweetAlert2 para el botón de confirmación
        });
    };


    const validarFormulario = () => {
        if (!user.trim() || !pssw.trim()) {
            showDataRequiredAlert();
            // showDataRequiredWithMew();
            // alert("Por favor, completa todos los campos.");
            return false;
        }

        return true;
    };

    const enviarDatos = (event) => {

        event.preventDefault();

        if (!validarFormulario()) {
            return;
        }

        axios.post("http://localhost:3001/login", {

            user: user,
            pssw: pssw

        }
        ).then(
            (resp) => {
                if (resp.data.msg === "No encontrado") {
                    alert("Usuario no registrado");
                } else if (resp.data.msg === "Encontrado") {
                    console.log(resp)
                    alert("Bienvenido");
                    nav("/Home")
                }
            }
        ).catch(() => {
            alert("Verifica los datos ingresados.");
        });
    };

    return (
        <>
            <div className='logbody'>
                <section class="logsec">
                    <h2>Bienvenido a Pokemón Training Zone</h2>
                    <form className='logform' onSubmit={enviarDatos}>
                        <label>Usuario:</label>
                        <input onChange={(e) => setUser(e.target.value)} type="text" id="usuario" placeholder="Ingresa tu usuario" />

                        <label>Contraseña:</label>
                        <input onChange={(e) => setPssw(e.target.value)} type="password" id="password" placeholder="Ingresa tu contraseña" />

                        <button type="submit">Iniciar Sesión</button>
                        <Link to="/register" className='loglink'>¿No tienes cuenta? Regístrate</Link>

                    </form>
                </section>
            </div>

        </>
    );
}

export default Login;
