import React from 'react';
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import './Styles/SidebarRight.css';
import Swal from 'sweetalert2';

const SidebarRight = () => {

    const AlertDef = (text) => {
        Swal.fire({
            title: '¡Aviso de la Pokédex!',
            text: text,
            imageUrl: 'ditto.gif',
            imageWidth: 150,
            imageHeight: 150,
            imageAlt: 'Imagen de ditto',
            timer: 2500,
            showConfirmButton: false,
            timerProgressBar: true,
            background: '#90c4d0',
            color: '#000000',
        });
    };

    const AlertErr = (text) => {
        Swal.fire({
            title: '¡Se ha escapado!',
            text: text,
            imageUrl: 'ditto.gif',
            imageWidth: 150,
            imageHeight: 150,
            imageAlt: 'Imagen de ditto',
            timer: 2500,
            showConfirmButton: false,
            timerProgressBar: true,
            background: '#ab3232',
            color: '#000000',
        });
    };

    const AlertTrue = (text) => {
        Swal.fire({
            title: '¡Capturado!',
            text: text,
            imageUrl: 'ditto.gif',
            imageWidth: 150,
            imageHeight: 150,
            imageAlt: 'Imagen de ditto',
            timer: 2000,
            showConfirmButton: false,
            timerProgressBar: true,
            background: '#3ea83d',
            color: '#000000',
        });
    };

    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [user, setUser] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("token");
        const userData = JSON.parse(localStorage.getItem("user"));

        if (token && userData) {
            setIsLoggedIn(true);
            setUser(userData);
        } else {
            setIsLoggedIn(false);
        }
    }, []);

    const [dataU, setDataU] = useState([]);

    useEffect(
        () => {
            axios.get("http://localhost:3001/gethomeuser", {
            }).then(
                (resp) => {
                    if (resp.data.msg === "Error") {
                        Aler("Error al obtener la info");
                    } else if (resp.data.msg === "No info") {
                        setDataU([]);
                        alert("No hay registros");
                    } else {
                        setDataU(resp.data);
                    }
                }
            )
            setUsElim(false);
    },[,ver, eliminarU]);

    return (
        <aside className="sidebar-right">
            {isLoggedIn ? (
                <>
                    <img className="profile-avatar" src="/pkball.png"></img>
                    <div className="mini-circles-list">
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                    </div>
                </>
            ) : (
                <>
                    <img className="profile-avatar" src="/pkball.png"></img>
                    <div className="mini-circles-list">
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                        <img className="circle-pair" src="/white.jpg"></img>
                        <img className="circle-pair2" src="/white.jpg"></img>
                    </div>
                </>
            )}

        </aside>
    );
};

export default SidebarRight;
