'use strict';

var define = require('define-properties');
var callBind = require('call-bind');

var implementation = require('array.prototype.flatmap/implementation');
var getPolyfill = require('array.prototype.flatmap/polyfill');
var polyfill = getPolyfill();
var shim = require('array.prototype.flatmap/shim');

var boundFlatMap = callBind(polyfill);

define(boundFlatMap, {
	getPolyfill: getPolyfill,
	implementation: implementation,
	shim: shim
});

module.exports = boundFlatMap;
